function() {
  var _date_key = [
      new Date().getFullYear(),
      ("0" + new Date().getMonth()).slice(-2),
      ("0" + new Date().getDate()).slice(-2),
      ("0" + new Date().getHours()).slice(-2),
      ("0" + new Date().getMinutes()).slice(-2),
    ];

    return _date_key.join("") + Math.floor(Math.random() * 99999);
}
